from __future__ import annotations

from typing import List

from app.parsing.graph import Step


MISSING_CONDITION = "\u0443\u0441\u043b\u043e\u0432\u0438\u0435 \u043d\u0435 \u0443\u043a\u0430\u0437\u0430\u043d\u043e"


def build_prompt(steps: List[Step]) -> str:
    lines: List[str] = []
    lines.append("\u0412\u0430\u043c \u0434\u0430\u043d \u0433\u0440\u0430\u0444 \u0434\u0438\u0430\u0433\u0440\u0430\u043c\u043c\u044b, \u0438\u0437\u0432\u043b\u0435\u0447\u0435\u043d\u043d\u044b\u0439 \u0438\u0437 \u0438\u0437\u043e\u0431\u0440\u0430\u0436\u0435\u043d\u0438\u044f.")
    lines.append("\u0421\u0433\u0435\u043d\u0435\u0440\u0438\u0440\u0443\u0439\u0442\u0435 \u0438\u0442\u043e\u0433\u043e\u0432\u043e\u0435 \u043e\u043f\u0438\u0441\u0430\u043d\u0438\u0435 \u0430\u043b\u0433\u043e\u0440\u0438\u0442\u043c\u0430 \u043d\u0430 \u0440\u0443\u0441\u0441\u043a\u043e\u043c \u044f\u0437\u044b\u043a\u0435.")
    lines.append("\u0412\u041d\u0418\u041c\u0410\u041d\u0418\u0415: \u041d\u0435 \u043f\u043e\u0432\u0442\u043e\u0440\u044f\u0439\u0442\u0435 \u0432\u0445\u043e\u0434\u043d\u044b\u0435 \u0434\u0430\u043d\u043d\u044b\u0435, \u043f\u0440\u0430\u0432\u0438\u043b\u0430 \u0438\u043b\u0438 \u0447\u0443\u0436\u0438\u0435 \u043f\u0440\u0438\u043c\u0435\u0440\u044b \u0432 \u043e\u0442\u0432\u0435\u0442\u0435.")
    lines.append("\u0417\u0430\u043f\u0440\u0435\u0449\u0435\u043d\u043e \u0443\u043f\u043e\u043c\u0438\u043d\u0430\u0442\u044c Graphviz, DOT, 'node', 'edge' \u0438 \u043b\u044e\u0431\u044b\u0435 \u0432\u043d\u0435\u0448\u043d\u0438\u0435 \u0438\u043d\u0441\u0442\u0440\u0443\u043c\u0435\u043d\u0442\u044b.")
    lines.append("\u041f\u0440\u0430\u0432\u0438\u043b\u0430 (\u043d\u0435 \u043f\u043e\u0432\u0442\u043e\u0440\u044f\u0442\u044c):")
    lines.append("- \u041d\u0435 \u0432\u044b\u0434\u0443\u043c\u044b\u0432\u0430\u0439\u0442\u0435 \u0448\u0430\u0433\u0438 \u0438 \u0443\u0441\u043b\u043e\u0432\u0438\u044f.")
    lines.append(
        "- \u0415\u0441\u043b\u0438 \u0443\u0441\u043b\u043e\u0432\u0438\u0435 \u043d\u0435 \u0443\u043a\u0430\u0437\u0430\u043d\u043e, \u043d\u0435 \u043f\u0438\u0448\u0438\u0442\u0435 \u0444\u0440\u0430\u0437\u0443 \"\u0443\u0441\u043b\u043e\u0432\u0438\u0435 \u043d\u0435 \u0443\u043a\u0430\u0437\u0430\u043d\u043e\" \u0438 \u043d\u0435 \u0443\u043f\u043e\u043c\u0438\u043d\u0430\u0439\u0442\u0435 \u0443\u0441\u043b\u043e\u0432\u0438\u0435 \u0432\u043e\u043e\u0431\u0449\u0435. \u041f\u0440\u043e\u0441\u0442\u043e \u043f\u0435\u0440\u0435\u0447\u0438\u0441\u043b\u0438\u0442\u0435 \u0438\u0441\u0445\u043e\u0434\u044b \u0432 \u043f\u043e\u0434\u043f\u0443\u043d\u043a\u0442\u0430\u0445."
    )
    lines.append("- \u0415\u0441\u043b\u0438 \u0438\u0441\u043f\u043e\u043b\u043d\u0438\u0442\u0435\u043b\u044c \u043d\u0435 \u0443\u043a\u0430\u0437\u0430\u043d, \u043d\u0435 \u0443\u043f\u043e\u043c\u0438\u043d\u0430\u0439\u0442\u0435 \u0435\u0433\u043e.")
    lines.append("- \u0414\u043b\u044f \u0440\u0430\u0437\u0432\u0438\u043b\u043e\u043a \u0443\u043a\u0430\u0436\u0438\u0442\u0435 \u0432\u0441\u0435 \u0432\u0435\u0442\u0432\u0438 \u0438 \u0438\u0445 \u0438\u0441\u0445\u043e\u0434\u044b.")
    lines.append("- \u0421\u0442\u0440\u043e\u0433\u043e \u0441\u043b\u0435\u0434\u0443\u0439\u0442\u0435 \u043f\u043e\u0440\u044f\u0434\u043a\u0443 \u0432\u0445\u043e\u0434\u043d\u043e\u0433\u043e \u0441\u043f\u0438\u0441\u043a\u0430 \u0443\u0437\u043b\u043e\u0432, \u043d\u0435 \u043c\u0435\u043d\u044f\u0439\u0442\u0435 \u043f\u043e\u0440\u044f\u0434\u043e\u043a.")
    lines.append("- \u0418\u0441\u043f\u043e\u043b\u044c\u0437\u0443\u0439\u0442\u0435 \u043d\u0443\u043c\u0435\u0440\u0430\u0446\u0438\u044e \u0432 \u0432\u0438\u0434\u0435 1., 2., 3. \u0438 \u043f\u043e\u0434\u043f\u0443\u043d\u043a\u0442\u044b 1.1, 1.2 \u0434\u043b\u044f \u0443\u0441\u043b\u043e\u0432\u0438\u0439.")
    lines.append("- \u0414\u043b\u044f \u0448\u043b\u044e\u0437\u043e\u0432 \u0438\u0441\u043f\u043e\u043b\u044c\u0437\u0443\u0439\u0442\u0435 \u0441\u043b\u043e\u0432\u043e \"\u0423\u0441\u043b\u043e\u0432\u0438\u0435\".")
    lines.append("- \u0415\u0441\u043b\u0438 \u0438\u0441\u043f\u043e\u043b\u043d\u0438\u0442\u0435\u043b\u044c \u0438\u0437\u0432\u0435\u0441\u0442\u0435\u043d, \u0434\u043e\u0431\u0430\u0432\u044c\u0442\u0435 \u0435\u0433\u043e \u0432 \u0441\u043a\u043e\u0431\u043a\u0430\u0445 \u0432 \u0444\u043e\u0440\u043c\u0435 \"(\u0438\u0441\u043f\u043e\u043b\u043d\u0438\u0442\u0435\u043b\u044c: ...)\". \u0415\u0441\u043b\u0438 \u043e\u043d \u043d\u0435 \u0443\u043a\u0430\u0437\u0430\u043d \u2014 \u0441\u043a\u043e\u0431\u043a\u0438 \u043d\u0435 \u043f\u0438\u0448\u0438\u0442\u0435.")
    lines.append("\u0424\u043e\u0440\u043c\u0430\u0442 \u043e\u0442\u0432\u0435\u0442\u0430:")
    lines.append("\u0420\u0415\u0417\u0423\u041b\u042c\u0422\u0410\u0422:")
    lines.append("1. ...")
    lines.append("1.1 \u0415\u0441\u043b\u0438: <\u0443\u0441\u043b\u043e\u0432\u0438\u0435> \u2192 <\u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0435>.")
    lines.append("1.2 <\u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0435>.")
    lines.append("2. ...")
    lines.append("")
    lines.append("\u0412\u0445\u043e\u0434\u043d\u044b\u0435 \u0434\u0430\u043d\u043d\u044b\u0435 (\u043d\u0435 \u043f\u043e\u0432\u0442\u043e\u0440\u044f\u0442\u044c \u0432 \u043e\u0442\u0432\u0435\u0442\u0435):")

    for step in steps:
        text_part = step.text if step.text else "(\u043d\u0435\u0442 \u0442\u0435\u043a\u0441\u0442\u0430)"
        actor_part = f" | \u0438\u0441\u043f\u043e\u043b\u043d\u0438\u0442\u0435\u043b\u044c: {step.actor}" if step.actor else ""
        lines.append(f"- {step.id} | {step.type} | {text_part}{actor_part}")
        if step.next:
            for target, condition in step.next:
                cond_text = condition if condition else MISSING_CONDITION
                lines.append(f"  -> {target} | \u0443\u0441\u043b\u043e\u0432\u0438\u0435: {cond_text}")
        else:
            lines.append("  -> (\u043d\u0435\u0442 \u0438\u0441\u0445\u043e\u0434\u044f\u0449\u0438\u0445)")

    lines.append("")
    lines.append("\u0412\u0435\u0440\u043d\u0438\u0442\u0435 \u0442\u043e\u043b\u044c\u043a\u043e \u0440\u0435\u0437\u0443\u043b\u044c\u0442\u0430\u0442 \u0432 \u0443\u043a\u0430\u0437\u0430\u043d\u043d\u043e\u043c \u0444\u043e\u0440\u043c\u0430\u0442\u0435.")
    return "\n".join(lines)
